import { Component,OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-addstudent',
  templateUrl: './addstudent.component.html',
  styleUrls: ['./addstudent.component.css']
})
export class AddstudentComponent implements OnInit {
  form!:FormGroup
  ngOnInit(): void {
    this.form = new FormGroup({
      username:new FormControl('',[Validators.required]),
      email:new FormControl(null ,[Validators.required,Validators.email]),
      password:new FormControl('',[Validators.required] )
    })
  }

}


            <div class="formDetails">
                <form [formGroup]="form">

                    <div id="userData" >
                        <input type="text" id="username" name="username" placeholder="USERNAME" formControlName="username"><br>
                        <!-- <p class="error" *ngIf="!form.get('username')?.valid && form.get ('username')?.touched">Username is required*</p> -->
                        <input type="password" id="password" name="password" placeholder="PASSWORD" formControlName="password"><br>
                        <!-- <p class="error" *ngIf="!form.get('fullname')?.valid && form.get ('fullname')?.touched">fullname is required*</p> -->
                        <input type="email" id="email" name="email" placeholder="EMAIL" formControlName="email"><br>
                        <!-- <p class="error" *ngIf="!form.get('email')?.valid && form.get ('email')?.touched">Email is required*</p> -->
                        <button type="button" class="btn">SUBMIT</button>
                    </div>
                </form>
            </div>
