import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-addstudent',
  templateUrl: './addstudent.component.html',
  styleUrls: ['./addstudent.component.css']
})
export class AddstudentComponent {

}


            <div class="formDetails">
                <form [formGroup]="form">

                    <div id="userData" >
                        <input type="text" id="username" name="username" placeholder="USERNAME" formControlName="username">
                        <!-- <p class="error" *ngIf="!form.get('username')?.valid && form.get ('username')?.touched">Username is required*</p> -->
                        <input type="text" id="fullname" name="fullname" placeholder="FULLNAME" formControlName="fullname">
                        <!-- <p class="error" *ngIf="!form.get('fullname')?.valid && form.get ('fullname')?.touched">fullname is required*</p> -->
                        <input type="email" id="email" name="email" placeholder="EMAIL" formControlName="email">
                        <!-- <p class="error" *ngIf="!form.get('email')?.valid && form.get ('email')?.touched">Email is required*</p> -->
                    </div>
                </form>
            </div>
